
public class IfForTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 1부터 100 사이에 존재하는 모든 짝수를 출력하기 
		 */
		
		// 1. 1부터 100까지 반복한다 
		for (int i=1; i<101; i++) {
			// 2.  i 값이 짝수인지 확인한다 
			if (i%2 == 0) {
				System.out.println(i);
				
			}
			// 3. i값이 짝수라면 출력한다 
			
			
		}
		
		
		
		
		
		
		
		
		
		
		
	}

}
