import java.util.Scanner;

public class Multimulti {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		
		
	
		
		System.out.println("======구구단 프로그램======");
		
		int l=2;
		while (l<10) {
			int i=0;
			while (i<9) {
				i++;
				System.out.println(l + "*" + i+ "=" + (l*i));
			}
			l++;
			
		}
		
		
		System.out.println("====================");
		System.out.println("구구구구구단을 출력합니다.");
		
		
				
				
		
		
	}

}
