
public class WhileToFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//while 기본 형식
		//1부터 10까지 출력
		
		int i =0;
		while (i<10) {
			i++;
			System.out.println(i);
		}
		
		//for 기본 형식
		// 1부터 10까지 출력
		for(int j=0; j <10; j++) // 초기;조건;증감식
		{
			System.out.println(j+1);
		}
		
		
	}

}
